#! /usr/bin/rebolREBOL [	Title:		"OpenCV Binding: imgcodecs_c"	Author:		"François Jouen"	Rights:		"Copyright (c) 2015 François Jouen. All rights reserved."	License: 	"BSD-3 - https:;github.com/dockimbel/Red/blob/master/BSD-3-License.txt"];flags when loading imageCV_LOAD_IMAGE_UNCHANGED:  -1 ;8bit, color or notCV_LOAD_IMAGE_GRAYSCALE:   0 ;8bit, grayCV_LOAD_IMAGE_COLOR:       1 ; ?, colorCV_LOAD_IMAGE_ANYDEPTH:    2 ;any depth, ? CV_LOAD_IMAGE_ANYCOLOR:    4 ;?, any color; save image to file flagsCV_IMWRITE_JPEG_QUALITY: 				1CV_IMWRITE_PNG_COMPRESSION: 			16CV_IMWRITE_PNG_STRATEGY: 				17CV_IMWRITE_PNG_STRATEGY_DEFAULT: 		0CV_IMWRITE_PNG_STRATEGY_FILTERED: 		1CV_IMWRITE_PNG_STRATEGY_HUFFMAN_ONLY: 	2CV_IMWRITE_PNG_STRATEGY_RLE: 			3CV_IMWRITE_PNG_STRATEGY_FIXED: 			4CV_IMWRITE_PXM_BINARY: 					32CV_IMWRITE_WEBP_QUALITY:				64; encodings convert one image to another CV_CVTIMG_FLIP:      		1CV_CVTIMG_SWAP_RB:   		2 CV_DEFAULT:			 		0cvLoadImage: cvFunc [name [string!] flag [integer!] return: [ptr!]] highgui "cvLoadImage" ; IplImagecvLoadImageM: cvFunc [name [string!] flag [integer!] return: [ptr!]] highgui "cvLoadImageM" ; CvMatcvSaveImage: cvFunc [filename [string!] image [ptr!] return: [integer!]] highgui "cvSaveImage"cvDecodeImage: cvFunc [buf [ptr!] iscolor [integer!] return: [ptr!]] highgui "cvDecodeImage" cvDecodeImageM: cvFunc [buf [ptr!] iscolor [integer!] return: [ptr!]] highgui "cvDecodeImageM" cvEncodeImage: cvFunc [ext [string!] image [ptr!] params [ptr!] return: [ptr!]] highgui "cvEncodeImage" cvConvertImage: cvFunc [src [ptr!] dst [ptr!] flags [integer!]] highgui "cvConvertImage" 