r!]		;CvHaarClassifierCascade!	pt_x                [integer!] ; CvPoint	pt_y                [integer!] ; CvPoint	start_stage         [integer!] ; CV_DEFAULT(0)	return:             [integer!]] objectd "cvRunHaarClassifierCascade";******************Latent SVM Object Detection functions*****************    