cvCalcAffineFlowPyrLK: cvFunc [	"Modification of a previous sparse optical flow algorithm to calculate affine flow"	prev 			[ptr!] 	curr 			[ptr!]	prev_pyr 		[ptr!] 	curr_pyr 		[ptr!]	prev_features	[ptr!] ;CvPoint2D32f*	curr_features	[ptr!] ;CvPoint2D32f*	matrices		[ptr!] ;real values 	count			[integer!]	win_size_w		[integer!] ; cvSize	win_size_h		[integer!] ; cvSize	level			[integer!]	status			[string!]	track_error		[ptr!]	criteria		[ptr!]	flags			[integer!]] video "cvCalcAffineFlowPyrLK"cvUpdateMotionHistory: cvFunc [	"Updates motion history image given motion silhouette"	silhouette		[ptr!]	mhi				[ptr!]	timestamp		[decimal!]	duration		[decimal!]] video "cvUpdateMotionHistory" ;;a mask indicating where the gradient is validcvCalcMotionGradient: cvFunc [	"Calculates gradient of the motion history image and fills"	mhi				[ptr!]	mask			[ptr!]	orientation		[ptr!]	delta1			[decimal!]	delta2			[decimal!]	aperture_size	[integer!] ; CV_DEFAULT(3)] video "cvCalcMotionGradient";(region can be selected by setting ROIs and/or by composing a valid gradient mask;with the region mask) cvCalcGlobalOrientation: cvFunc [	"Calculates average motion direction within a selected motion region"	orientation		[ptr!]	mask			[ptr!]	mhi				[ptr!]	timestamp		[decimal!]	duration		[decimal!]	return:			[decimal!]] video "cvCalcGlobalOrientation";(e.g. left hand, right hand)cvSegmentMotion: cvFunc [	"Splits a motion history image into a few parts corresponding to separate independent motions"	mhi				[ptr!]	seg_mask		[ptr!]	storage			[ptr!]		;CvMemStorage*	timestamp		[decimal!]	seg_thresh		[decimal!]	return:			[ptr!] 		; CvSeq*] video "cvSegmentMotion" 