: cvFunc [capture [ptr!] return: [integer!]] videoio "cvGrabFrame" cvRetrieveFrame: cvFunc [capture [ptr!] streamIdx [integer!] return: [ptr!]] videoio "cvRetrieveFrame"cvQueryFrame: cvFunc [capture [ptr!] return: [ptr!] ] videoio "cvQueryFrame" cvReleaseCapture: cvFunc [capture [ptr!]] videoio "cvReleaseCapture"cvGetCaptureProperty: cvFunc [capture [ptr!] property_id [integer!] return: [decimal!]] videoio "cvGetCaptureProperty"cvSetCaptureProperty: cvFunc [capture [ptr!] property_id [integer!] value [decimal!] return: [integer!]] videoio "cvSetCaptureProperty"cvGetCaptureDomain: cvFunc [capture [ptr!] return: [integer!]] videoio "cvGetCaptureDomain"cvCreateVideoWriter: cvFunc [filename [string!] fourcc [integer!]  pfs [decimal!] width [integer!] height [integer!] is_color [integer!] return: [ptr!]] videoio "cvCreateVideoWriter"	cvWriteFrame: cvFunc [writer [ptr!] image [ptr!] return: [integer!]] videoio "cvWriteFrame"cvReleaseVideoWriter: cvFunc [writer [ptr!]] videoio "cvReleaseVideoWriter"