#! /usr/local/bin/rebolREBOL [	Title:		"OpenCV Tests: Sort"	Author:		"François Jouen"	Rights:		"Copyright (c) 2012-2014 François Jouen. All rights reserved."	License:    "BSD-3 - https://github.com/dockimbel/Red/blob/master/BSD-3-License.txt"]do %../opencv.rset 'appDir what-dir wName: "Image 1 [space to continue]"picture:  to-string to-local-file join appDir "_images/baboon.jpg"img: cvLoadImage picture CV_LOAD_IMAGE_COLORimgS: getPointerValues img IplImage!s0: cvCreateImage imgS/width imgS/height IPL_DEPTH_8U 1s1: cvCreateImage imgS/width imgS/height IPL_DEPTH_8U 1s2: cvCreateImage imgS/width imgS/height IPL_DEPTH_8U 1ds0: cvCreateImage imgS/width imgS/height IPL_DEPTH_8U 1ds1: cvCreateImage imgS/width imgS/height IPL_DEPTH_8U 1ds2: cvCreateImage imgS/width imgS/height IPL_DEPTH_8U 1img2: cvCreateImage imgS/width imgS/height 8 3cvShowImage wname imgcvNamedWindow "Sorted Image" CV_WINDOW_AUTOSIZEcvSplit img s0 s1 s2 none ; OK; cvSort doesn't work !cvSort s0 ds0 none  CV_SORT_EVERY_ROW + CV_SORT_DESCENDINGcvSort s1 ds1 none  CV_SORT_EVERY_ROW + CV_SORT_DESCENDINGcvSort s2 ds2 none  CV_SORT_EVERY_ROW + CV_SORT_DESCENDINGcvMerge ds0 ds1 ds2 none img2cvShowImage "Sorted Image" img2cvWaitKey 0