#! /usr/local/bin/rebolREBOL [	Title:		"OpenCV Tests: Random functions "	Author:		"François Jouen"	Rights:		"Copyright (c) 2012-2016 François Jouen. All rights reserved."	License:    "BSD-3 - https://github.com/dockimbel/Red/blob/master/BSD-3-License.txt"]do %../opencv.rset 'appDir what-dir wName: "Image 1 [space to continue]"depth: IPL_DEPTH_32F; size: make struct! CvSize! [640 480]img: cvCreateImage size/width size/height depth 3imgS: getPointerValues img IplImage!cvZero imgcvNamedWindow wName CV_WINDOW_AUTOSIZE cvShowImage wName img cvWaitKey 0cvRNG to-integer now/timep1: cvScalar 0 0 0 0p2: cvScalar random 255  random 255 random 255 random 255val: random 255ptr: pointer 'integer! random 255&ptr: struct-address? ptr;CV_RAND_UNIcvRandArr &ptr img CV_RAND_UNI p1/v0 p1/v1 p1/v2 p1/v3 p2/v0 p2/v1 p2/v2 p2/v3cvShowImage wname imgcvWaitKey 0;CV_RAND_NORMALcvRandArr &ptr img CV_RAND_NORMAL p1/v0 p1/v1 p1/v2 p1/v3 p2/v0 p2/v1 p2/v2 p2/v3cvShowImage wname imgcvWaitKey 0cvRandShuffle img &ptr 1.0cvShowImage wname imgcvWaitKey 0print "Done"free-mem imgfreeDylib